import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Person } from '../person';
import { PersonLsService } from '../person-ls.service';

@Component({
  selector: 'app-details',
  templateUrl: './details.component.html',
  styleUrls: ['./details.component.css'],
})
export class DetailsComponent implements OnInit {
  personId?: number;
  person?: Person;

  constructor(
    private route: ActivatedRoute,
    private personLsService: PersonLsService
  ) {}

  ngOnInit() {
    this.route.params.subscribe((params) => {
      this.personId = params['id'];
      this.person = this.personId
        ? this.personLsService.getPerson(this.personId)
        : undefined;
    });
  }
}
<div class="details-container">
  <div>
    <a class="back" [routerLink]="'/'">&laquo; Back</a>
  </div>

  <div *ngIf="person" class="person">
    <h2>{{ person.firstName }} {{ person.lastName }}</h2>
    <dl class="info">
      <dt>ID:</dt>
      <dd>{{ personId }}</dd>

      <dt>Age:</dt>
      <dd>{{ person.age }}</dd>
    </dl>

    <dl *ngIf="person.address" class="address">
      <dt>City:</dt>
      <dd>{{ person.address.city }}</dd>

      <dt>Street:</dt>
      <dd>{{ person.address.street }}</dd>

      <dt>Postcode:</dt>
      <dd>{{ person.address.postCode }}</dd>
    </dl>
  </div>

  <div class="alert" *ngIf="!person">Person not found!</div>
</div>
